#!/bin/bash # установить инструменты разработки командной строки macOS sudo xcode-select --install; # установить homebrew /bin/bash -c "$(curl -fsSL https://raw.githubusercontent.com/Homebrew/install/master/install.sh)"; # дополнительные источники пакетов homebrew brew tap elastic/tap; brew tap shivammathur/php; # установить пакеты homebrew brew install pkg-config mariadb httpd mailhog imagemagick elastic/tap/elasticsearch-full; # установить php 5.6 brew install shivammathur/php/php@5.6; pecl install xdebug-2.5.5; printf "
" | pecl install imagick; # установить php 7.4 brew install shivammathur/php/php@7.4; pecl install xdebug; printf "
" | pecl install imagick; # установить php 8.0 brew install shivammathur/php/php@8.0; pecl install xdebug; # эта команда не работает с PHP 8.0 на момент написания printf "
" | pecl install imagick; # запустить и настроить mariadb brew services start mariadb; sudo /usr/local/bin/mysql_upgrade; sudo /usr/local/bin/mysql_secure_installation; # # На этом этапе отредактируйте файлы конфигурации для Apache и PHP, # смотрите «Сводку конфигурации» ниже. # # запустить остальные сервисы brew services start elasticsearch-full; brew services start php@5.6; brew services start php@7.4; brew services start php@8.0; brew services start httpd;
